import React, { useEffect, useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

const RainDrop = ({ delay }) => (
  <motion.div
    initial={{ y: -20, x: Math.random() * window.innerWidth, opacity: 0 }}
    animate={{ 
      y: window.innerHeight,
      opacity: [0, 1, 1, 0],
    }}
    transition={{ 
      duration: 1.5,
      delay,
      repeat: Infinity,
      ease: "linear"
    }}
    className="absolute w-0.5 h-3 bg-blue-400/30 rounded-full"
  />
);

const Snowflake = ({ delay }) => (
  <motion.div
    initial={{ y: -20, x: Math.random() * window.innerWidth, opacity: 0 }}
    animate={{ 
      y: window.innerHeight,
      x: [null, (Math.random() - 0.5) * 200],
      opacity: [0, 1, 1, 0],
      rotate: 360
    }}
    transition={{ 
      duration: 3,
      delay,
      repeat: Infinity,
      ease: "linear"
    }}
    className="absolute text-white/50"
  >
    ‚ùÑ
  </motion.div>
);

const SunRay = ({ angle }) => (
  <motion.div
    initial={{ opacity: 0, scale: 0.8 }}
    animate={{ 
      opacity: [0.3, 0.7, 0.3],
      scale: [0.8, 1.1, 0.8],
    }}
    transition={{ 
      duration: 3,
      repeat: Infinity,
      ease: "easeInOut"
    }}
    className="absolute w-40 h-1 bg-gradient-to-r from-yellow-400/50 to-transparent"
    style={{ 
      transformOrigin: 'left center',
      transform: `rotate(${angle}deg)`,
      left: '50%',
      top: '50%'
    }}
  />
);

const WeatherEffects = ({ condition }) => {
  const [particles, setParticles] = useState([]);

  useEffect(() => {
    let count;
    switch (condition) {
      case 'rain':
      case 'drizzle':
        count = 50;
        setParticles(Array.from({ length: count }, (_, i) => ({
          id: i,
          delay: Math.random() * 2
        })));
        break;
      case 'snow':
        count = 30;
        setParticles(Array.from({ length: count }, (_, i) => ({
          id: i,
          delay: Math.random() * 3
        })));
        break;
      case 'sunny':
      case 'clear':
        count = 12;
        setParticles(Array.from({ length: count }, (_, i) => ({
          id: i,
          angle: (i * 360) / count
        })));
        break;
      default:
        setParticles([]);
    }
  }, [condition]);

  return (
    <div className="fixed inset-0 pointer-events-none overflow-hidden">
      <AnimatePresence>
        {particles.map((particle) => {
          switch (condition) {
            case 'rain':
            case 'drizzle':
              return <RainDrop key={particle.id} delay={particle.delay} />;
            case 'snow':
              return <Snowflake key={particle.id} delay={particle.delay} />;
            case 'sunny':
            case 'clear':
              return <SunRay key={particle.id} angle={particle.angle} />;
            default:
              return null;
          }
        })}
      </AnimatePresence>
    </div>
  );
};

export default WeatherEffects;
