import axios from 'axios';

const BASE_URL = 'https://api.open-meteo.com/v1';

const GEOCODING_URL = 'https://geocoding-api.open-meteo.com/v1/search';

const weatherService = {
    // Get coordinates for a city
    getCoordinates: async (city) => {
        try {
            const response = await axios.get(
                `${GEOCODING_URL}?name=${encodeURIComponent(city)}&count=1`
            );
            if (response.data.results && response.data.results.length > 0) {
                return response.data.results[0];
            }
            throw new Error('City not found');
        } catch (error) {
            console.error('Error fetching coordinates:', error);
            throw error;
        }
    },

    getWeatherData: async (latitude, longitude) => {
        try {
            const response = await axios.get(`${BASE_URL}/forecast`, {
                params: {
                    latitude,
                    longitude,
                    hourly: 'temperature_2m,relative_humidity_2m,precipitation_probability,weather_code',
                    daily: 'weather_code,temperature_2m_max,temperature_2m_min,precipitation_probability_max',
                    current_weather: true,
                    timezone: 'auto',
                    forecast_days: 5
                }
            });

            const { current_weather, hourly, daily } = response.data;

            // Convert weather codes to conditions
            const getCondition = (code) => {
                if (code <= 3) return 'sunny';
                if (code <= 48) return 'cloudy';
                if (code <= 67) return 'rain';
                if (code <= 77) return 'snow';
                if (code <= 82) return 'rain';
                if (code <= 86) return 'snow';
                if (code <= 99) return 'thunderstorm';
                return 'sunny';
            };

            // Get weather icon based on condition and hour
            const getWeatherIcon = (condition, hour) => {
                const isNight = hour < 6 || hour > 18;
                switch (condition) {
                  case 'sunny':
                    return isNight ? 'üåô' : '‚òÄÔ∏è';
                  case 'cloudy':
                    return '‚òÅÔ∏è';
                  case 'rain':
                    return 'üåßÔ∏è';
                  case 'snow':
                    return '‚ùÑÔ∏è';
                  case 'thunderstorm':
                    return '‚õàÔ∏è';
                  default:
                    return '‚òÄÔ∏è';
                }
            };

            // Process current weather
            const current = {
                temperature: Math.round(current_weather.temperature),
                windSpeed: Math.round(current_weather.windspeed),
                condition: getCondition(current_weather.weathercode),
                humidity: hourly.relative_humidity_2m[0] // Use first hour's humidity
            };

            // Process 24 hours of forecast
            const hourlyData = [];
            for (let i = 0; i < 24; i++) {
                const hour = new Date(hourly.time[i]).getHours();
                const condition = getCondition(hourly.weather_code[i]);
                hourlyData.push({
                    time: new Date(hourly.time[i]).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }),
                    temp: Math.round(hourly.temperature_2m[i]),
                    condition: condition,
                    icon: getWeatherIcon(condition, hour)
                });
            }

            // Process 5-day forecast
            const dailyData = daily.time.map((date, index) => {
                const condition = getCondition(daily.weather_code[index]);
                return {
                    date,
                    maxTemp: daily.temperature_2m_max[index],
                    minTemp: daily.temperature_2m_min[index],
                    condition,
                    icon: getWeatherIcon(condition, 12), // Use noon as reference time
                    precipitationProbability: Math.round(daily.precipitation_probability_max[index])
                };
            });

            return {
                current,
                hourly: hourlyData,
                daily: dailyData
            };
        } catch (error) {
            console.error('Error fetching weather data:', error);
            throw error;
        }
    },

    // Combined function to get weather for a city
    getWeatherForCity: async (city) => {
        try {
            const coordinates = await weatherService.getCoordinates(city);
            const weatherData = await weatherService.getWeatherData(
                coordinates.latitude,
                coordinates.longitude
            );
            return {
                ...weatherData,
                cityInfo: {
                    name: coordinates.name,
                    country: coordinates.country
                }
            };
        } catch (error) {
            console.error('Error fetching weather for city:', error);
            throw error;
        }
    }
};

export { weatherService };
