import axios from 'axios';

const BASE_URL = 'https://api.open-meteo.com/v1';

const GEOCODING_URL = 'https://geocoding-api.open-meteo.com/v1/search';

const weatherService = {
    // Get coordinates for a city
    getCoordinates: async (city) => {
        try {
            const response = await axios.get(
                `${GEOCODING_URL}?name=${encodeURIComponent(city)}&count=1`
            );
            if (response.data.results && response.data.results.length > 0) {
                return response.data.results[0];
            }
            throw new Error('City not found');
        } catch (error) {
            console.error('Error fetching coordinates:', error);
            throw error;
        }
    },

    getWeatherData: async (latitude, longitude) => {
        try {
            const response = await axios.get(`${BASE_URL}/forecast`, {
                params: {
                    latitude,
                    longitude,
                    hourly: 'temperature_2m,relative_humidity_2m,precipitation_probability,weather_code,windspeed_10m',
                    daily: 'weather_code,temperature_2m_max,temperature_2m_min,precipitation_probability_max,relative_humidity_max,windspeed_10m_max',
                    current_weather: true,
                    timezone: 'auto',
                    forecast_days: 5
                }
            });

            const { current_weather, hourly, daily } = response.data;

            // Convert weather codes to detailed conditions
            const getCondition = (code) => {
                // WMO Weather interpretation codes (https://open-meteo.com/en/docs)
                if (code === 0) return 'clear';
                if (code === 1) return 'sunny';
                if (code === 2) return 'partly-cloudy';
                if (code === 3) return 'cloudy';
                if (code >= 45 && code <= 48) return 'fog';
                if (code >= 51 && code <= 55) return 'drizzle';
                if (code >= 61 && code <= 65) return 'rain';
                if (code >= 71 && code <= 77) return 'snow';
                if (code >= 80 && code <= 82) return 'rain';
                if (code >= 85 && code <= 86) return 'snow';
                if (code >= 95) return 'thunderstorm';
                return 'clear';
            };

            // Check if it's daytime based on sunrise/sunset
            const isDay = (timestamp) => {
                const date = new Date(timestamp);
                const hour = date.getHours();
                const currentDayIndex = daily.time.findIndex(day => new Date(day).getDate() === date.getDate());

                if (currentDayIndex === -1) return true;

                const sunrise = new Date(daily.sunrise[currentDayIndex]);
                const sunset = new Date(daily.sunset[currentDayIndex]);

                return date >= sunrise && date <= sunset;
            };

            // Process current weather
            const current = {
                temperature: Math.round(current_weather.temperature),
                windSpeed: Math.round(current_weather.windspeed),
                condition: getCondition(current_weather.weathercode),
                isDay: isDay(new Date()),
                humidity: hourly.relative_humidity_2m[0]
            };

            // Process 24 hours of forecast
            const hourlyData = [];
            for (let i = 0; i < 24; i++) {
                const timestamp = hourly.time[i];
                const condition = getCondition(hourly.weather_code[i]);
                hourlyData.push({
                    time: new Date(timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }),
                    temp: Math.round(hourly.temperature_2m[i]),
                    condition,
                    isDay: isDay(timestamp)
                });
            }

            // Process 5-day forecast
            const dailyData = daily.time.map((date, index) => {
                const condition = getCondition(daily.weather_code[index]);
                return {
                    date,
                    maxTemp: daily.temperature_2m_max[index],
                    minTemp: daily.temperature_2m_min[index],
                    condition,
                    isDay: true, // Always true for daily forecast icons
                    precipitationProbability: Math.round(daily.precipitation_probability_max[index]),
                    humidity: Math.round(daily.relative_humidity_max[index]),
                    windSpeed: Math.round(daily.windspeed_10m_max[index]),
                    sunrise: new Date(daily.sunrise[index]).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }),
                    sunset: new Date(daily.sunset[index]).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
                };
            });

            return {
                current,
                hourly: hourlyData,
                daily: dailyData
            };
        } catch (error) {
            console.error('Error fetching weather data:', error);
            throw error;
        }
    },

    // Combined function to get weather for a city
    getWeatherForCity: async (city) => {
        try {
            const coordinates = await weatherService.getCoordinates(city);
            const weatherData = await weatherService.getWeatherData(
                coordinates.latitude,
                coordinates.longitude
            );
            return {
                ...weatherData,
                cityInfo: {
                    name: coordinates.name,
                    country: coordinates.country
                }
            };
        } catch (error) {
            console.error('Error fetching weather for city:', error);
            throw error;
        }
    }
};

export { weatherService };
