import React from 'react';
import { motion } from 'framer-motion';

const WeatherForecast = ({ forecast }) => {
  return (
    <div className="bg-white/90 backdrop-blur-md rounded-lg shadow-lg p-6">
      <h2 className="text-2xl font-semibold mb-4 text-gray-800">Today's Forecast</h2>

      <div className="grid grid-cols-5 gap-4 overflow-x-auto pb-2">
        {forecast.slice(0, 5).map((item, index) => (
          <motion.div
            key={index}
            className="text-center p-3 rounded-lg hover:bg-gray-50 transition-colors"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: index * 0.1 }}
            whileHover={{ scale: 1.05 }}
          >
            <p className="text-gray-600 mb-2 font-medium">{item.time}</p>
            <motion.div 
              className="text-3xl mb-2 mx-auto w-fit"
              animate={{ rotate: [0, 10, 0] }}
              transition={{ duration: 2, repeat: Infinity, ease: "easeInOut" }}
            >
              {item.icon}
            </motion.div>
            <p className="text-lg font-semibold bg-gradient-to-r from-blue-500 to-blue-700 text-transparent bg-clip-text">
              {Math.round(item.temp)}°C
            </p>
          </motion.div>
        ))}
      </div>

      <div className="mt-6 pt-4 border-t">
        <div className="flex justify-between items-center text-sm text-gray-600">
          <span>Updated {new Date().toLocaleTimeString()}</span>
          <motion.button 
            className="text-blue-500 hover:text-blue-700 font-medium"
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            View Full Forecast →
          </motion.button>
        </div>
      </div>
    </div>
  );
};

export default WeatherForecast;
