import React from 'react';
import { motion } from 'framer-motion';

const DailyForecast = ({ dailyForecast }) => {
  const getDayName = (dateStr) => {
    const days = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
    const date = new Date(dateStr);
    return days[date.getDay()];
  };

  return (
    <div className="bg-white/90 backdrop-blur-md rounded-lg shadow-lg p-6 mt-6">
      <h2 className="text-2xl font-semibold mb-4">5-Day Forecast</h2>
      <div className="grid grid-cols-5 gap-4">
        {dailyForecast.map((day, index) => (
          <motion.div
            key={day.date}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: index * 0.1 }}
            className="flex flex-col items-center p-4 bg-white/50 rounded-lg hover:bg-white/70 transition-colors"
          >
            <span className="text-gray-600 font-medium mb-2">
              {index === 0 ? 'Today' : getDayName(day.date)}
            </span>
            <div className="text-4xl mb-2">{day.icon}</div>
            <div className="flex flex-col items-center">
              <span className="text-xl font-bold text-gray-800">
                {Math.round(day.maxTemp)}Â°
              </span>
              <span className="text-sm text-gray-500">
                {Math.round(day.minTemp)}Â°
              </span>
            </div>
            <div className="mt-2 text-xs text-gray-600 text-center">
              <div>ðŸ’§ {day.precipitationProbability}%</div>
            </div>
          </motion.div>
        ))}
      </div>
    </div>
  );
};

export default DailyForecast;
