import React from 'react';
import { motion } from 'framer-motion';

const WeatherForecast = ({ forecast }) => {
  return (
    <div className="bg-white rounded-lg shadow-lg p-6">
      <h2 className="text-2xl font-semibold mb-4 text-gray-800">Today's Forecast</h2>

      <div className="grid grid-cols-5 gap-4">
        {forecast.map((item, index) => (
          <motion.div
            key={index}
            className="text-center"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: index * 0.1 }}
          >
            <p className="text-gray-600 mb-2">{item.time}</p>
            <div className="text-3xl text-blue-500 mb-2">
              {item.icon}
            </div>
            <p className="text-lg font-semibold">{item.temp}Â°C</p>
          </motion.div>
        ))}
      </div>

      <div className="mt-6 pt-4 border-t">
        <div className="flex justify-between text-sm text-gray-600">
          <span>Updated 5 mins ago</span>
          <button className="text-blue-500 hover:text-blue-700">
            View Full Forecast
          </button>
        </div>
      </div>
    </div>
  );
};

export default WeatherForecast;
