import React, { useState } from 'react';
import { motion } from 'framer-motion';
import WeatherCard from './WeatherCard';
import WeatherForecast from './WeatherForecast';
import CitySearch from './CitySearch';
import { WiDaySunny, WiRain, WiSnow, WiCloudy } from 'react-icons/wi';

const WeatherDashboard = () => {
  const [selectedCity, setSelectedCity] = useState('London');
  const [weatherData, setWeatherData] = useState({
    temp: 22,
    condition: 'sunny',
    humidity: 65,
    windSpeed: 12,
    uvIndex: 4
  });

  // Mock data for demonstration
  const mockForecast = [
    { time: '9 AM', temp: 20, icon: <WiDaySunny /> },
    { time: '12 PM', temp: 23, icon: <WiCloudy /> },
    { time: '3 PM', temp: 25, icon: <WiRain /> },
    { time: '6 PM', temp: 21, icon: <WiDaySunny /> },
    { time: '9 PM', temp: 19, icon: <WiCloudy /> }
  ];

  return (
    <motion.div 
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      className="container mx-auto p-4 max-w-6xl"
    >
      <h1 className="text-4xl font-bold text-center mb-8 text-gray-800">
        Weatherly
      </h1>

      <CitySearch onCitySelect={setSelectedCity} />

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mt-8">
        <WeatherCard 
          city={selectedCity}
          temperature={weatherData.temp}
          condition={weatherData.condition}
          humidity={weatherData.humidity}
          windSpeed={weatherData.windSpeed}
          uvIndex={weatherData.uvIndex}
        />

        <WeatherForecast forecast={mockForecast} />
      </div>

      <div className="mt-8 bg-white rounded-lg shadow-lg p-6">
        <h2 className="text-2xl font-semibold mb-4">Weather Recommendations</h2>
        <div className="text-gray-700">
          {weatherData.temp > 25 ? (
            <p>üåû Perfect day for outdoor activities! Don't forget your sunscreen!</p>
          ) : weatherData.temp < 15 ? (
            <p>‚ùÑÔ∏è It's a bit chilly! Consider wearing warm clothes.</p>
          ) : (
            <p>üå§Ô∏è Pleasant weather! Great time for a walk!</p>
          )}
        </div>
      </div>
    </motion.div>
  );
};

export default WeatherDashboard;
