import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { WiDaySunny, WiRain, WiSnow, WiCloudy } from 'react-icons/wi';
import FullForecast from './FullForecast';

const WeatherForecast = ({ forecast }) => {
  const [isFullForecastOpen, setIsFullForecastOpen] = useState(false);

  const getWeatherIcon = (condition) => {
    switch (condition) {
      case 'rain':
        return <WiRain className="text-4xl text-blue-500" />;
      case 'snow':
        return <WiSnow className="text-4xl text-blue-200" />;
      case 'cloudy':
        return <WiCloudy className="text-4xl text-gray-500" />;
      default:
        return <WiDaySunny className="text-4xl text-yellow-500" />;
    }
  };

  return (
    <>
      <div className="bg-white/90 backdrop-blur-md rounded-lg p-6 shadow-lg">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-xl font-semibold text-gray-800">Hourly Forecast</h2>
          <button
            onClick={() => setIsFullForecastOpen(true)}
            className="text-blue-500 hover:text-blue-600 transition-colors text-sm font-medium"
          >
            View Full Forecast →
          </button>
        </div>
        <div className="grid grid-cols-5 gap-4">
          {forecast.map((hour, index) => (
            <motion.div
              key={index}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: index * 0.1 }}
              className="text-center"
            >
              <div className="text-sm font-medium text-gray-600 mb-2">{hour.time}</div>
              <motion.div
                whileHover={{ scale: 1.1 }}
                className="flex justify-center mb-2"
              >
                {getWeatherIcon(hour.condition)}
              </motion.div>
              <div className="text-lg font-bold text-gray-800">{hour.temp}°C</div>
            </motion.div>
          ))}
        </div>
      </div>

      <FullForecast
        forecast={forecast}
        isOpen={isFullForecastOpen}
        onClose={() => setIsFullForecastOpen(false)}
      />
    </>
  );
};

export default WeatherForecast;
