import React, { useState } from 'react';
import { motion } from 'framer-motion';

const CitySearch = ({ onCitySelect }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [suggestions] = useState([
    'London', 'New York', 'Tokyo', 'Paris', 'Sydney',
    'Dubai', 'Singapore', 'Mumbai', 'Toronto', 'Berlin'
  ]);

  const filteredSuggestions = suggestions.filter(city =>
    city.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="relative">
      <div className="flex items-center justify-center">
        <motion.input
          type="text"
          placeholder="Search for a city..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="w-full max-w-md px-4 py-2 text-gray-700 bg-white border rounded-lg focus:outline-none focus:border-blue-500"
          whileFocus={{ scale: 1.02 }}
          transition={{ type: "spring", stiffness: 300 }}
        />
      </div>

      {searchTerm && (
        <motion.div
          initial={{ opacity: 0, y: -10 }}
          animate={{ opacity: 1, y: 0 }}
          className="absolute z-10 w-full max-w-md mt-2 bg-white rounded-lg shadow-lg mx-auto left-0 right-0"
        >
          {filteredSuggestions.map((city, index) => (
            <motion.div
              key={city}
              className="px-4 py-2 cursor-pointer hover:bg-gray-100"
              whileHover={{ backgroundColor: '#f3f4f6' }}
              onClick={() => {
                onCitySelect(city);
                setSearchTerm('');
              }}
            >
              {city}
            </motion.div>
          ))}
        </motion.div>
      )}
    </div>
  );
};

export default CitySearch;
