import React from 'react';
import { motion } from 'framer-motion';
import { useNavigate, useLocation } from 'react-router-dom';
import WeatherIcon from './WeatherIcon';
import WeatherEffects from './WeatherEffects';

const DetailedForecast = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const forecast = location.state?.forecast || [];

  const getDayName = (dateStr) => {
    const days = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
    const date = new Date(dateStr);
    return days[date.getDay()];
  };

  if (forecast.length === 0) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-500 to-blue-700 flex items-center justify-center">
        <div className="text-white text-center">
          <h1 className="text-3xl font-bold mb-4">No forecast data available</h1>
          <button
            onClick={() => navigate('/')}
            className="px-6 py-3 bg-white text-blue-600 rounded-lg hover:bg-blue-50 transition-colors"
          >
            Return to Dashboard
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-500 to-blue-700">
      <WeatherEffects condition={forecast[0].condition} />

      <div className="container mx-auto p-4">
        <div className="flex justify-between items-center mb-8">
          <h1 className="text-4xl font-bold text-white">5-Day Forecast</h1>
          <button
            onClick={() => navigate('/')}
            className="px-4 py-2 bg-white/20 hover:bg-white/30 text-white rounded-lg transition-colors flex items-center gap-2"
          >
            <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
              <path fillRule="evenodd" d="M9.707 16.707a1 1 0 01-1.414 0l-6-6a1 1 0 010-1.414l6-6a1 1 0 011.414 1.414L5.414 9H17a1 1 0 110 2H5.414l4.293 4.293a1 1 0 010 1.414z" clipRule="evenodd" />
            </svg>
            Back to Dashboard
          </button>
        </div>

        <div className="space-y-6">
          {forecast.map((day, index) => (
            <motion.div
              key={day.date}
              initial={{ x: -50, opacity: 0 }}
              animate={{ x: 0, opacity: 1 }}
              transition={{ delay: index * 0.1 }}
              className="bg-white/90 backdrop-blur-md rounded-lg shadow-lg p-6"
            >
              <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
                {/* Day and Main Weather */}
                <div className="col-span-1 md:col-span-2">
                  <div className="flex items-center gap-4">
                    <motion.div
                      animate={{ rotate: [0, 5, 0] }}
                      transition={{ duration: 2, repeat: Infinity, ease: "easeInOut" }}
                    >
                      <WeatherIcon condition={day.condition} size="text-6xl" />
                    </motion.div>
                    <div>
                      <h2 className="text-2xl font-bold">
                        {index === 0 ? 'Today' : getDayName(day.date)}
                      </h2>
                      <p className="text-gray-600 capitalize">{day.condition}</p>
                    </div>
                  </div>
                </div>

                {/* Temperature */}
                <div className="text-center">
                  <p className="text-gray-600 mb-2">Temperature</p>
                  <div className="flex items-center justify-center gap-4">
                    <div>
                      <WeatherIcon condition="temperature" size="text-2xl" className="text-red-500" />
                      <p className="text-xl font-bold">{Math.round(day.maxTemp)}¬∞</p>
                      <p className="text-sm text-gray-500">High</p>
                    </div>
                    <div>
                      <WeatherIcon condition="temperature" size="text-2xl" className="text-blue-500" />
                      <p className="text-xl font-bold">{Math.round(day.minTemp)}¬∞</p>
                      <p className="text-sm text-gray-500">Low</p>
                    </div>
                  </div>
                </div>

                {/* Additional Info */}
                <div className="text-center">
                  <p className="text-gray-600 mb-2">Details</p>
                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <WeatherIcon condition="precipitation" size="text-2xl" className="text-blue-500 mx-auto" />
                      <p className="text-lg font-semibold">{day.precipitationProbability}%</p>
                      <p className="text-sm text-gray-500">Precipitation</p>
                    </div>
                    <div>
                      <WeatherIcon condition="sunny" size="text-2xl" className="text-yellow-500 mx-auto" />
                      <div className="text-sm">
                        <p>‚Üë {day.sunrise}</p>
                        <p>‚Üì {day.sunset}</p>
                      </div>
                    </div>
                  </div>
                </div>
              </div>

              {/* Recommendations */}
              <div className="mt-4 pt-4 border-t border-gray-200">
                <p className="text-gray-700">
                  {day.maxTemp > 25 ? (
                    <span>‚òÄÔ∏è High temperature expected. Stay hydrated and use sun protection.</span>
                  ) : day.maxTemp < 15 ? (
                    <span>‚ùÑÔ∏è Cool weather ahead. Consider wearing warm layers.</span>
                  ) : (
                    <span>üå§Ô∏è Pleasant weather expected. Great conditions for outdoor activities.</span>
                  )}
                  {day.precipitationProbability > 50 && (
                    <span> üåÇ High chance of precipitation - keep an umbrella handy!</span>
                  )}
                </p>
              </div>
            </motion.div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default DetailedForecast;
