import React from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { WiDaySunny, WiRain, WiSnow, WiCloudy } from 'react-icons/wi';

const FullForecast = ({ forecast, isOpen, onClose }) => {
  const getWeatherIcon = (condition) => {
    switch (condition) {
      case 'rain':
        return <WiRain className="text-4xl text-blue-500" />;
      case 'snow':
        return <WiSnow className="text-4xl text-blue-200" />;
      case 'cloudy':
        return <WiCloudy className="text-4xl text-gray-500" />;
      default:
        return <WiDaySunny className="text-4xl text-yellow-500" />;
    }
  };

  return (
    <AnimatePresence>
      {isOpen && (
        <>
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 0.5 }}
            exit={{ opacity: 0 }}
            className="fixed inset-0 bg-black"
            onClick={onClose}
          />
          <motion.div
            initial={{ opacity: 0, scale: 0.8 }}
            animate={{ opacity: 1, scale: 1 }}
            exit={{ opacity: 0, scale: 0.8 }}
            className="fixed inset-4 md:inset-16 bg-white rounded-xl shadow-2xl overflow-auto"
          >
            <div className="p-6">
              <div className="flex justify-between items-center mb-6">
                <h2 className="text-2xl font-bold text-gray-800">24-Hour Forecast</h2>
                <button
                  onClick={onClose}
                  className="text-gray-500 hover:text-gray-700 transition-colors"
                >
                  <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                  </svg>
                </button>
              </div>

              <div className="grid grid-cols-2 md:grid-cols-4 lg:grid-cols-6 gap-4">
                {forecast.map((hour, index) => (
                  <motion.div
                    key={index}
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ delay: index * 0.05 }}
                    className="bg-blue-50 rounded-lg p-4 text-center"
                  >
                    <div className="font-medium text-gray-800 mb-2">{hour.time}</div>
                    <div className="flex justify-center mb-2">
                      {getWeatherIcon(hour.condition)}
                    </div>
                    <div className="text-2xl font-bold text-blue-600">{hour.temp}Â°C</div>
                    <div className="text-sm text-gray-600 capitalize mt-1">{hour.condition}</div>
                  </motion.div>
                ))}
              </div>
            </div>
          </motion.div>
        </>
      )}
    </AnimatePresence>
  );
};

export default FullForecast;
